import React, { useState, useEffect } from 'react';
import axios from "axios";


axios.interceptors.request.use(
    (config) => {
        config.headers.authorization = `Bearer ${localStorage.getItem("jwtToken")}`;
        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);


const SchoolList = (id) => {

    const [result, setResult] = useState([]);

    useEffect(() => {
        const fetchRequest = async () => {
            console.log("TTTTTTTTTTTTTTTTTTTTT", id.id)
            try {
                if(id.id == "all") {
                    await axios
                    .get("https://tutorhelper20210920193710.azurewebsites.net/api/v1/areas")        
                    .then((response) => {
                        setResult(response.data.schools)
                        console.log("RRRRRRRRRRRRRR", response.data)
                    });

                } else {
                    await axios
                    .get(`https://tutorhelper20210920193710.azurewebsites.net/api/v1/areas/${id.id}`)
                    .then((response) => {
                        setResult(response.data.schools)
                        console.log("QQQQQQQQQQQQQQQQ", response.data.schools)
                    });
                    return (
                        <div className="col-11">
                            <table className="table table-bordered " >
                                <thead>
                                    <tr>
                                        <th>No.</th>
                                        <th>School</th>
                                        <th>Address</th>
                                        <th>Type</th>
                                    </tr>
                                </thead>
                                <tbody>
                                {result.map((item, id) => {
                                    return [
                                        <tr>
                                            <td>{id + 1}</td>
                                            <td>{item.schoolName}</td>
                                            <td>{item.address}</td>
                                            <td>
                                                {item.schoolLevel == 12 ? "High school": "Secondary school"}
                                            </td>
                                            
                                        </tr>
                                    ]
                                })}
                            </tbody>
                            </table>
                        </div>
                    )
                }
            } catch (error) {
                console.log(error);
            }
        }
        fetchRequest();
    }, [id])




    
}

export default SchoolList
