import React, { useState, useEffect } from "react";
import "./tutor-request.scss";
import axios from "axios";
import GetAuthor from './GetAuthor';

axios.interceptors.request.use(
    (config) => {
        config.headers.authorization = `Bearer ${localStorage.getItem("jwtToken")}`;
        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);

function getStudentById(id) {
    axios
        .get(`https://tutorhelper20210920193710.azurewebsites.net/api/v1/students/${id}`)
        .then((response) => {
            return response.data.data.fullName;
        })
        .catch(err => {
            console.log(err);
        });
}


const TutorRequest = () => {
    const [request, setRequest] = useState([]);

    useEffect(() => {
        const fetchRequest = async () => {
            try {
                axios
                    .get("https://tutorhelper20210920193710.azurewebsites.net/api/v1/tutor-requests", {
                        params: {
                            PageSize: 100,
                        }
                    })
                    .then((response) => {
                        setRequest(response.data.data);
    
                    });
    
            } catch (error) {
                console.log(error);
            }
        }
        fetchRequest();
    }, []);

    // const OnChangeStatus = (requestId, requestStatus) => {
    //     useEffect(() => {
    //         try {
    //             axios
    //                 .put(
    //                     "https://tutorhelper20210920193710.azurewebsites.net/api/v1/tutor-requests",
    //                     {
    //                         tutorRequestId: requestId,
    //                         status: requestStatus
    //                     }
    //                 )
    //                 .then((response) => {
    //                     setRequest(response.data.data);

    //                 });
    //         } catch (error) {
    //             console.log("Error");
    //         }
    //     }, []);
    // 

    return (
        <div className="col-11">
            <table className="table table-bordered " >
                <thead>
                    <tr>
                        <th>No.</th>
                        <th>Content</th>
                        <th>Author</th>
                        <th>Create Date</th>
                        <th>Status</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {request.map((item, id) => {
                        return [

                            <tr>
                                <td>{id + 1}</td>
                                <td>
                                    <p>
                                        <h3>{item.title}</h3>
                                        {item.description}
                                    </p>
                                    <a href="#" className="link">view all</a>
                                </td>
                                {/* <td>{item.studentId}</td> */}
                                <td>
                                    <GetAuthor id={item.studentId}/>
                                </td>
                                <td>{item.createAt}</td>
                                <td className="text">{item.status}</td>
                                <td className="action">
                                    <button type="button" className="approve">
                                        Approve
                                    </button>
                                    <button type="button" className="reject">
                                        Reject
                                    </button>
                                </td>
                            </tr>
                        ]
                    })}
                </tbody>
            </table>
        </div>
    );
};

export default TutorRequest;
