import React, { useState, useEffect } from "react";
import "./tutor-request.scss";
import { Table, Input, InputNumber, Popconfirm, Form, Typography } from 'antd';

import tutorRequestApi from "../../../api/tutorRequestApi";
import axios from "axios";


axios.interceptors.request.use(
    (config) => {
        config.headers.authorization = `Bearer ${localStorage.getItem("jwtToken")}`;
        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);


const Card = () => {
    const [request, setRequest] = useState([]);

    useEffect(() => {
        try {
            axios
                .get(
                    "https://tutorhelper20210920193710.azurewebsites.net/api/v1/tutor-requests", {
                        params: {
                            PageSize: 100
                        }
                    }
                )
                .then((response) => {
                    setRequest(response.data.data);

                });
        } catch (error) {
            console.log("Error");
        }
    }, []);

    const OnChangeStatus = (requestId, requestStatus) => {
        useEffect(() => {
            try {
                axios
                    .put(
                        "https://tutorhelper20210920193710.azurewebsites.net/api/v1/tutor-requests",
                        {
                            tutorRequestId: requestId,
                            status: requestStatus
                        }
                    )
                    .then((response) => {
                        setRequest(response.data.data);

                    });
            } catch (error) {
                console.log("Error");
            }
        }, []);
    }

    return (
        // <div className="col-11">
        //   <table>
        //     <thead>
        //       <tr>
        //         <th>No.</th>
        //         <th>Content</th>
        //         <th>Author</th>
        //         <th>Create Date</th>
        //         <th>Status</th>
        //         <th>Action</th>
        //       </tr>
        //     </thead>
        //     {request.map((item, id) => {
        //       return [

        //           <tr>
        //             <td>{id}</td>
        //             <td>
        //               <p>
        //                 <h3>{item.title}</h3>
        //                 {item.description}
        //               </p>
        //               <a className="link">view all</a>
        //             </td>
        //             <td>{item.studentId}</td>
        //             <td>{item.createAt}</td>
        //             <td className="text">Pending</td>
        //             <td className="action">
        //               <button type="button" className="approve">
        //                 Approve
        //               </button>
        //               <button type="button" className="reject">
        //                 Reject
        //               </button>
        //             </td>
        //           </tr>

        //       ]
        //     })}
        //   </table>
        // </div>
        <div className="col-11">
            <table className="table table-bordered" border="1">
                <thead>
                    <tr>
                        <th>No.</th>
                        <th>Content</th>
                        <th>Author</th>
                        <th>Create Date</th>
                        <th>Status</th>
                        <th>Action</th>
                    </tr>
                </thead>
                {request.map((item, id) => {
                    return [

                        <tr>
                            <td>{id}</td>
                            <td>
                                <p>
                                    <h3>{item.title}</h3>
                                    {item.description}
                                </p>
                                <a className="link">view all</a>
                            </td>
                            <td>{item.studentId}</td>
                            <td>{item.createAt}</td>
                            <td className="text">Pending</td>
                            <td className="action">
                                <button type="button" className="approve" >
                                    Approve
                                </button>
                                <button type="button" className="reject">
                                    Reject
                                </button>
                            </td>
                        </tr>
                      
                    ]
                })}
            </table>
        </div>
    );

};

const TutorRequest = () => {
    return (
        <div>
            <h3>List request</h3>
            <Card />
        </div>
    );
};

export default TutorRequest;
