import React, {usetState, useEffect} from 'react';
import "./tutor-request.scss"
import axios from "axios";

axios.interceptors.request.use(
    (config) => {
        config.headers.authorization = `Bearer ${localStorage.getItem("jwtToken")}`;
        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);

const Action = (requestUpdate) => {

    console.log("TTTTTTTTTTTT", requestUpdate)
    // useEffect(() => {
    //     const fetchRequest = async () => {
    //         try {
    //             await axios
    //                 .get(`https://tutorhelper20210920193710.azurewebsites.net/api/v1/tutor-requests/${id}`)
    //                 .then((response) => {
    //                     axios.put

    //                 });

    //         } catch (error) {
    //             console.log(error);
    //         }
    //     }
    //     fetchRequest();
    // }, []);


    if (requestUpdate.status == "Pending") {
        return (
            <div className="action">
                <button type="button" className="approve" >
                    Approve
                </button>
                <button type="button" className="reject" >
                    Reject
                </button>
            </div>
        )
    } else if (requestUpdate.status == "Approved") {
        return (
            <div className="action">
                <button type="button" className="delete" >
                    Delete
                </button>
            </div>
        )
    } else if(requestUpdate.status == "Reject") {
        return <div></div>
    } else if(requestUpdate.status == "Accepted") {
        return <div></div>
    } else if(requestUpdate.status == "Deleted") {
        return <div></div>
    }




}

export default Action
