  return (
    <div className="col-11">
      <table>
        <thead>
          <tr>
            <th>No.</th>
            <th>Content</th>
            <th>Author</th>
            <th>Create Date</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        {request.map((item, id) => {
          return [
            <tbody>
              <tr>
                <td>{id}</td>
                <td>
                  <p>
                    <h3>{item.title}</h3>
                    {item.description}
                  </p>
                  <a className="link">view all</a>
                </td>
                <td>{item.studentId}</td>
                <td>{item.createAt}</td>
                <td className="text">Pending</td>
                <td className="action">
                  <button type="button" className="approve">
                    Approve
                  </button>
                  <button type="button" className="reject">
                    Reject
                  </button>
                </td>
              </tr>
            </tbody>
          ]
        })}
      </table>
    </div>
  );
};

const TutorRequest = () => {
  return (
    <div>
      <h3>List request</h3>
      <Card />
    </div>
  );
};

export default TutorRequest;
