import React, { useState, useEffect } from 'react';
import "./dropdown.scss";
import axios from "axios";

import { Menu, Dropdown, Button } from "antd";


axios.interceptors.request.use(
    (config) => {
        config.headers.authorization = `Bearer ${localStorage.getItem("jwtToken")}`;
        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);

const ChooseArea = () => {
    const [listArea, setListArea] = useState([]);

    useEffect(() => {
        const fetchRequest = async () => {
            try {
                await axios
                    .get("https://tutorhelper20210920193710.azurewebsites.net/api/v1/areas", {
                        params: {
                            PageSize: 100,
                        }
                    })
                    .then((response) => {
                        setListArea(response.data)
                    });

            } catch (error) {
                console.log(error);
            }
        }
        fetchRequest();
    }, [])

    return (
        <div>
            <div className="dropdown">
                <p className="t">Choose area: </p>
                <button className="dropbtn">Area</button>
                <div className="dropdown-content">
                    {listArea.map((item, id) => {
                        return [
                            <a className="dropdown-item" href={id}>{item.areaName}</a>
                        ]
                    })}
                </div>
            </div>




            {/* {listArea.map((item, id) => {
                return [
                    <li><a class="dropdown-item" href={id}>{item.areaName}</a></li>
                ]
            })} */}
        </div>
    )
}

export default ChooseArea;
