import React, { useState, useEffect } from "react";
import "./style.scss";

import tutorRequestApi from "../../../api/tutorRequestApi";
import axios from "axios";

axios.interceptors.request.use(
  (config) => {
    config.headers.authorization = `Bearer ${localStorage.getItem("jwtToken")}`;
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

const GetListRequest = () => {
  // const [request, setRequest] = useState([]);

  // useEffect(() => {
  //   try {
  //     axios
  //       .get(
  //         "https://tutorhelper20210920193710.azurewebsites.net/api/v1/tutor-requests"
  //       )
  //       .then((response) => {
  //         setRequest(response.data.data);     
  //         console.log(request);
  //       });
  //   } catch (error) {
  //     console.log("Error");
  //   }
  // }, []);
};

const Card = () => {
  // GetListRequest();
  const [request, setRequest] = useState([]);

  useEffect(() => {
    try {
      axios
        .get(
          "https://tutorhelper20210920193710.azurewebsites.net/api/v1/tutor-requests"
        )
        .then((response) => {
           
           console.log(response.data.data);   
           setRequest(response.data.data);       
           
        });
    } catch (error) {
      console.log("Error");
    }
  }, []);
  return (
    <div className="col-11">
      <table border="1">
        <tr>
          <th>No.</th>
          <th>Content</th>
          <th>Status</th>
          <th>Action</th>
        </tr>
        {request.map((id, item) => {
          <tr>
          <td>{id}</td>
          <td>
            <p>
              {item.title}
             
            </p>
            <a href="#">view all</a>
          </td>
          <td className="text">Pending</td>
          <td className="action">
            <button type="button" className="approve">
              Approve
            </button>
            <button type="button" className="reject">
              Reject
            </button>
          </td>
        </tr>
        })}
      </table>
    </div>
  );
};
const TutorRequest = () => {
  return (
    <div>
      <h3>List request</h3>
      <Card />
    </div>
  );
};

export default TutorRequest;
