import axios from "axios";

const axiosClient = axios.create({
  //baseUrl: "https://tutorhelper20210920193710.azurewebsites.net/",
  baseUrl: "https://tutorhelper20210920193710.azurewebsites.net/swagger/index.html?fbclid=IwAR2qAGBowooUbb_HeO4OqMXlMEld2BoCdhOIumuwbpmuELg37Ohv0lG0TNo/api/v1",
  headers: {
    "Content-Type": "application/json",
  },
});

// Add a request interceptor
axiosClient.interceptors.request.use(
  function (config) {
    // Do something before request is sent
    return config;
  },
  function (error) {
    // Do something with request error

    return Promise.reject(error);
  }
);

// Add a response interceptor
axiosClient.interceptors.response.use(
  function (response) {
    // Any status code that lie within the range of 2xx cause this function to trigger
    // Do something with response data
    return response.data;
  },
  function (error) {
    // Any status codes that falls outside the range of 2xx cause this function to trigger
    // Do something with response error
    return Promise.reject(error);
  }
);

export default axiosClient;
