import React, { useState, useEffect } from "react";
import "./tutor.scss";
import axios from "axios";


axios.interceptors.request.use(
    (config) => {
        config.headers.authorization = `Bearer ${localStorage.getItem("jwtToken")}`;
        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);


const Card = () => {
       //Get current result
       const indexOfLastResult = currentPage * resultPerPage;
       const indexOfFirstResult = indexOfLastResult - resultPerPage;
       const currentResult = tutor.slice(indexOfFirstResult, indexOfLastResult);
   
       //Change page 
       const paginate = (pageNumber) => setCurrentPage(pageNumber);
   
       return (
           <div>
   
               {tutor.map((item, id) => {
                   return [
                       <div className="card cardTutor col-3">
                           <img src={item.imagePath} width="70" height="70" alt="Tutor image" />
                           <div className="p">
                               <h3>{item.fullName}</h3>
                               <p >
                                   {item.email}<br />
                                   {item.phoneNumber}
                               </p>
                           </div>
                           <div>
   
                           </div>
   
                           <button type="button" className="btnView">View all</button>
                       </div>
                   ]
               })}
               <Pagination resultPerPage={resultPerPage} totalresult={tutor.length} paginate={paginate} />
           </div>
   
       );
}



const Tutor = () => {
    const [tutor, setTutor] = useState([]);
    const [loading, setLoading] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [resultPerPage, setResultPerPage] = useState(10);

    useEffect(() => {
        const fetchRequest = async () => {
            setLoading(true);
            try {
                const respone = await axios
                    .get("https://tutorhelper20210920193710.azurewebsites.net/api/v1/tutors", {
                        params: {
                            PageNume: currentPage,
                            PageSize: resultPerPage
                        }
                    })
                    .then((response) => {
                        setTutor(response.data.data);
                        setLoading(false);
                    });
            } catch (error) {
                console.log(error);
            }
        };
        fetchRequest();
    }, []);

    return (
        <div></div>
    )
 
};

export default Tutor;
