import React from 'react';
import "./tutor.scss";
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';

const Card = ({ result, loading }) => {

  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  if (loading) {
    return <h2>Loading...</h2>
  }

  return (
    <div>
      {result.map((item) => {
        return [
          <div className="card cardTutor col-3">
            <div>
              {/* <div className="header-left"> */}
              <img src={item.imagePath} width="70" height="70" alt="Tutor image" style={{ marginBottom: 80 }} />
              {/* </div> */}
              <div className="header-right">
                <h5>{item.fullName}</h5>
                <p>
                  {item.email}<br />
                  {item.phoneNumber}
                </p>
              </div>
            </div>

            <button type="button" className="btnView" onClick={handleOpen}>View all</button>

            <div class="modal" tabindex="-1">
              <div class="modal-dialog">
                <div class="modal-content">
                  <div class="modal-header">
                    <h5 class="modal-title">Modal title</h5>
                    <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                  </div>
                  <div class="modal-body">
                    <p>Modal body text goes here.</p>
                  </div>
                  <div class="modal-footer">
                    <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                    <button type="button" class="btn btn-primary">Save changes</button>
                  </div>
                </div>
              </div>
            </div>

          </div>
        ]
      })}

    </div>

  );
}

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #ccc',
  boxShadow: 24,
  p: 4,
};

export default Card;
